@use 'abstracts' as *;

*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
  box-sizing: border-box;
}

.container {
  padding: 2rem;
  max-width: 140rem;
  margin: auto;
}

@media only screen and (max-width: $bp-tablet) {
  .container {
    padding: 3rem;
  }
}

@media only screen and (max-width: $bp-mobile) {
  .container {
    padding: 2rem 2rem 5rem;
  }
}

