sources.scss/layout/_editor.scss:
@use 'abstracts' as *;

.editor {
  display: flex;
  flex-direction: column;
  margin: 0 3rem;
  width: 60%;

  &__container {
    background: $color-secondary;
    border-radius: 0.8rem;
    margin-bottom: 3rem;
    padding: 3rem;
    position: relative;
  }

  &__area {
    background-color: #141414;
    border: 1px solid #141414;
    border-top: 4rem solid #141414;
    border-radius: 8px;
    box-shadow: 0px 16px 24px rgba(0, 0, 0, 0.24);
    padding: 0 1rem 0;
    width: 100%;
  }

  &__code {
    background-color: #141414;
    font-size: $font-size-code;
    font-family: $font-family-code;
    width: 100%;
    white-space: pre-wrap;

    &:focus {
      outline: none;
    }

    &::-webkit-scrollbar {
      width: 10px;
    }

    &::-webkit-scrollbar-track {
      background: rgba(44, 44, 44, 0.9);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb {
      background: rgba(29, 29, 29, 0.8);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb:hover {
      background: rgba(0, 0, 0, 0.1);
    }
  }

  &__buttons {
    position: absolute;
    top: 4.5rem;
    left: 4.5rem;

    &:hover {
      cursor: pointer;
    }
  }
}

.export {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: 2rem;

  &__title {
    margin-top: 2rem;
    font-size: 1.4rem;
    letter-spacing: 3px;
    text-align: center;
    font-weight: lighter;
  }

  & > * {
    width: calc((100% / 2) - 1%);
  }
}

@media only screen and (max-width: $bp-tablet) {
  .editor {
    margin: 0;
    width: 100%;
  }
}

@media only screen and (max-width: $bp-mobile) {
  .editor {
    &__area {
      border-top: 3rem solid #141414;
    }
  }

  .export {
    flex-direction: column;

    & > * {
      width: 100%;

      &:last-child {
        margin-top: 2rem;
      }
    }
  }
}

sources.scss/layout/_project.scss:
@use 'abstracts' as *;

.project {
  display: flex;
  flex-direction: column;
  width: 20%;

  &__info {
    margin-bottom: 4rem;
  }

  &__info > *:not(:last-child) {
    margin-bottom: 2rem;
  }

