sources.scss/base/_base.scss:
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
  box-sizing: border-box;
}

.container {
  padding: 2rem;
  max-width: 140rem;
  margin: auto;
}

sources.scss/components/_buttons.scss:
@use '../abstracts' as *;

.btn {
  color: $color-text;
  font-size: $font-size-default;
  font-family: $font-family;
  padding: 2rem 2rem;
  background: rgba($color-tertiary, 0.08);
  border-radius: 0.8rem;
  border: 4px solid transparent;
  transition: all 0.2s ease;
}

.btn-dark {
  &:hover {
    background: rgba($color-tertiary, 0.16);
    cursor: pointer;
  }

  &:active {
    border: $border-btn-dark-pressed;
  }
}

.btn-light {
  background-color: $color-tertiary;
  color: $color-primary;
  cursor: pointer;

  &:hover {
    background: $color-btn-light-hover;
  }

  &:active {
    border: $border-btn-light-pressed;
  }
}

sources.scss/abstracts/_variables.scss:
$darkred: darkred;
$color-primary: #051d3b;
$color-secondary: #6bd1ff;
$color-tertiary: #5081fb;
$color-text: #fff;
$color-text-greyish: rgba(255, 255, 255, 0.64);
$color-input-bg: rgba(255, 255, 255, 0.16);
$color-input-bg-hover: rgba(255, 255, 255, 0.24);

$color-btn-light-hover: #7ba4fc;
$border-btn-light-pressed: 4px solid rgba(80, 129, 251, 0.72);
$border-btn-dark-pressed: 4px solid rgba(80, 129, 251, 0.24);

$font-size-default: 1.6rem;
$font-family: 'Inter', sans-serif;
$font-size-code: 1.4rem;
$font-family-code: 'Roboto mono', monospace;

sources.scss/components/_input.scss:
@use 'abstracts' as *;

.input {
  color: $color-text-greyish;
  font-size: $font-size-default;
  font-family: $font-family;
  height: 5.6rem;
  width: 100%;

  padding: 1.5rem;
  background: $color-input-bg;
  border-radius: 8px;
  border: none;
  outline: none;
  transition: all 0.2s ease;

  &::placeholder {
    color: $color-text-greyish;
  }

  &:hover,
  &:focus {
    background: $color-input-bg-hover;
  }
}

sources.scss/layout/_header.scss:
@use 'abstracts' as *;

.header {
  display: flex;
  align-items: center;
  justify-content: space-between;

  &__logo {
    width: 20%;
  }

  &__user {
    width: 20%;
  }

  .logo {
    height: 3.4rem;
    width: 14.5rem;
  }

  &__form {
    margin: 0 3rem;
    width: 60%;
  }

  .search {
    color: $color-text;
    font-size: $font-size-default;
    height: 5.6rem;
    width: 100%;

    padding: 2rem;
    background: $color-input-bg;
    border-radius: 8px;
    border: none;
    outline: none;
    transition: all 0.2s ease;

    &:hover,
    &:focus {
      background: $color-input-bg-hover;
    }
  }

  .user {
    display: flex;
    align-items: center;
    justify-content: flex-end;
    padding: 1rem 2rem;
    margin-left: auto;
    max-width: 14.5rem;

    &-name {
      color: $color-text;
      font-size: $font-size-default;
      font-weight: lighter;
      margin-left: 1rem;
    }

    &:hover {
      background: rgba(#fff, 0.08);
      border-radius: 8px;
      cursor: pointer;
    }
  }
}

sources.scss/layout/_menu.scss:
@use 'abstracts' as *;

//FIXME
.content {
  display: flex;
  justify-content: space-between;
  margin-top: 5rem;
}

.menu {
  display: flex;
  flex-direction: column;
  width: 20%;

  & > *:not(:last-child) {
    margin-bottom: 2rem;
  }

  &__title {
    font-size: 1.2rem;
    text-transform: uppercase;
    font-weight: lighter;
  }

  &__item {
    display: flex;
    align-items: center;

    &-name {
      font-size: 1.6rem;
      opacity: 0.56;
    }

    &:hover {
      cursor: pointer;
    }

    &:hover &-name {
      opacity: 0.8;
    }

    &:hover .menu__icon {
      background: rgba($color-tertiary, 0.64);
    }
  }

  &__icon {
    background: $color-tertiary;
    border-radius: 1.6rem;
    margin-right: 1rem;
    padding: 1.2rem;
    opacity: 0.16;
  }
}

.active > * {
  opacity: 1;
}

sources.scss/layout/_editor.scss:
@use 'abstracts' as *;

.editor {
  display: flex;
  flex-direction: column;
  margin: 0 3rem;
  width: 60%;

  &__container {
    background: $color-secondary;
    border-radius: 0.8rem;
    margin-bottom: 3rem;
    padding: 3rem;
    position: relative;
  }

  &__code {
    background-color: #000;
    border-top: 4rem solid #000;
    border-radius: 8px;
    color: $color-text;
    font-size: $font-size-code;
    font-family: $font-family-code;
    padding: 0 2rem 2rem;
    resize: none;
    width: 100%;

    &:focus {
      outline: none;
    }

    &::-webkit-scrollbar {
      width: 10px;
    }

    &::-webkit-scrollbar-track {
      background: rgba(0, 0, 0, 0.9);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb {
      background: rgba(0, 0, 0, 0.2);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb:hover {
      background: rgba(0, 0, 0, 0.1);
    }
  }

  &__buttons {
    position: absolute;
    top: 4.5rem;
    left: 4.5rem;

    &:hover {
      cursor: pointer;
    }
  }
}

sources.scss/layout/_project.scss:
@use 'abstracts' as *;

.project {
  display: flex;
  flex-direction: column;
  width: 20%;

  &__info {
    margin-bottom: 4rem;
  }

  &__info > *:not(:last-child) {
    margin-bottom: 2rem;
  }

  &__info,
  &__settings {
    display: flex;
    flex-direction: column;
  }

  &__info h3,
  &__settings h3 {
    font-size: 1.2rem;
    font-weight: lighter;
    text-transform: uppercase;
  }

  &__select {
    background: $color-input-bg;
    border: none;
    border-radius: 8px;
    color: $color-text-greyish;
    font-size: $font-size-default;
    font-family: $font-family;
    height: 5.6rem;
    outline: none;
    padding: 1rem;

    &:hover {
      background: $color-input-bg-hover;
    }
  }

  &__option {
    background-color: rgba($color-primary, 0.8);
    border: none;
    border-radius: 8px;
    color: $color-text;
    outline: none;
    padding: 1rem;

    &:hover {
      background-color: $color-primary;
    }
  }

  &__settings {
    & > *:not(:last-child) {
      margin-bottom: 2.5rem;
    }
  }

  &__description {
    height: 100%;
    resize: none;
  }

  &__color-picker {
    background: transparent;
    border: 1px solid #fff;
    border-radius: 8px;
    height: 5.6rem;
    padding: 0.3rem 0.4rem;
    width: 100%;

    &::-webkit-color-swatch {
      border-radius: 4px;
    }
  }
}
