sources.scss/layout/_menu.scss:
@use 'abstracts' as *;

.menu {
  display: flex;
  flex-direction: column;
  list-style: none;
  width: 20%;

  & > *:not(:last-child) {
    margin-bottom: 2rem;
  }

  &__title {
    font-size: 1.2rem;
    text-transform: uppercase;
    font-weight: lighter;
  }

  &__item {
    display: flex;
    align-items: center;
    color: $color-text;
    text-decoration: none;

    &-name {
      font-size: 1.6rem;
      opacity: 0.56;
    }

    &:hover &-name {
      opacity: 0.8;
    }

    &:hover .menu__icon {
      background: rgba($color-tertiary, 0.64);
    }
  }

  &__icon {
    background: $color-tertiary;
    border-radius: 1.6rem;
    margin-right: 1rem;
    padding: 1.2rem;
    opacity: 0.16;
  }
}

.active > * {
  opacity: 1;
}

/* Animation for dropdown menu and search bar */

@keyframes menuIn {
  0% {
    opacity: 0;
    transform: translateX(40%);
  }
  100% {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes menuOut {
  0% {
    opacity: 1;
    transform: translateX(0);
  }
  100% {
    opacity: 0;
    transform: translateX(40%);
  }
}

@media only screen and (max-width: $bp-tablet) {
  .menu {
    display: none;
    opacity: 0;
    width: 100%;
    align-items: flex-end;
    margin-bottom: 4rem;

    &__item {
      width: 188.28px;
    }

    &__icon {
      margin-left: 1rem;
    }
  }

  .show-menu-mobile {
    display: flex;
    opacity: 1;
    animation: menuIn 0.5s ease-in-out;
  }

  .hide-menu-mobile {
    display: flex;
    opacity: 0;
    animation: menuOut 0.5s ease-in-out;
  }
}

sources.scss/layout/_editor.scss:
@use 'abstracts' as *;

.editor {
  display: flex;
  flex-direction: column;
  margin: 0 3rem;
  width: 60%;

  &__container {
    background: $color-secondary;
    border-radius: 0.8rem;
    margin-bottom: 3rem;
    padding: 3rem;
    position: relative;
  }

  &__code {
    background-color: #141414;
    border: 1px solid #141414;
    border-top: 4rem solid #141414;
    border-radius: 8px;
    box-shadow: 0px 16px 24px rgba(0, 0, 0, 0.24);
    color: $color-text;
    font-size: $font-size-code;
    font-family: $font-family-code;
    padding: 0 2rem 2rem;
    resize: none;
    width: 100%;

    &:focus {
      outline: none;
    }

    &::-webkit-scrollbar {
      width: 10px;
    }

    &::-webkit-scrollbar-track {
      background: rgba(44, 44, 44, 0.9);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb {
      background: rgba(29, 29, 29, 0.8);
      border-radius: 8px;
    }

    &::-webkit-scrollbar-thumb:hover {
      background: rgba(0, 0, 0, 0.1);
    }
  }

  &__buttons {
    position: absolute;
    top: 4.5rem;
    left: 4.5rem;

    &:hover {
      cursor: pointer;
    }
  }
}

@media only screen and (max-width: $bp-tablet) {
  .editor {
    margin: 0;
    width: 100%;
  }
}

sources.scss/layout/_project.scss:
@use 'abstracts' as *;

.project {
  display: flex;
  flex-direction: column;
  width: 20%;

  &__info {
    margin-bottom: 4rem;
  }

  &__info > *:not(:last-child) {
    margin-bottom: 2rem;
  }

  &__info,
  &__settings {
    display: flex;
    flex-direction: column;
  }

  &__info h3,
  &__settings h3 {
    font-size: 1.2rem;
    font-weight: lighter;
    text-transform: uppercase;
  }

  &__select {
    background: $color-input-bg;
    border: none;
    border-radius: 8px;
    color: $color-text-greyish;
    font-size: $font-size-default;
    font-family: $font-family;
    height: 5.6rem;
    outline: none;
    padding: 1rem;

    &:hover {
      background: $color-input-bg-hover;
    }
  }

  &__select-group {
    position: relative;
  }

  &__select-button {
    color: $color-text-greyish;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: $color-input-bg;
    border: none;
    border-radius: 8px;
    font-family: $font-family;
    font-size: $font-size-default;
    padding: 1rem 1.5rem;
    height: 5.6rem;
    width: 100%;

    &:hover {
      background: $color-input-bg-hover;
    }

    &:focus {
      outline: none;
    }
  }

  &__select-arrow {
    border: solid $color-text-greyish;
    border-width: 0 2px 2px 0;
    padding: 4px;
    transform: rotate(45deg);
  }

  &__dropdown {
    border: 2px solid #5875b4;
    border-radius: 0.8rem;
    display: flex;
    flex-direction: column;
    position: absolute;
    width: 100%;
  }

  &__option {
    background-color: rgba($color-primary, 0.8);
    border: none;
    outline: none;
    padding: 1rem;

    &:hover {
      background-color: $color-primary;
    }
  }

  &__radio-option {
    display: none;
  }

  &__select-item {
    background: #5875b4;
    color: $color-text-greyish;
    font-size: $font-size-default;
    border: 6px solid #5875b4;

    &:hover {
      background: lightskyblue;

      &:first-of-type {
        border-top-left-radius: 0.8rem;
        border-top-right-radius: 0.8rem;
      }

      &:last-of-type {
        border-bottom-left-radius: 0.8rem;
        border-bottom-right-radius: 0.8rem;
      }
    }
  }

  &__settings {
    & > *:not(:last-child) {
      margin-bottom: 2.5rem;
    }
  }

  &__customize {
    display: flex;
    flex-direction: column;

    & > *:first-child {
      margin-bottom: 2.5rem;
    }
  }

  &__description {
    height: 100%;
    resize: none;
  }

  &__color-picker {
    background: transparent;
    border: 1px solid #fff;
    border-radius: 8px;
    height: 5.6rem;
    padding: 0.3rem 0.4rem;
    width: 100%;

    &::-webkit-color-swatch {
      border: none;
      border-radius: 4px;
    }
  }
}

#button {
  margin-bottom: 0;
}

.hidden {
  display: none;
}

@media only screen and (max-width: $bp-tablet) {
  .project {
    margin-top: 5rem;
    width: 100%;

    &__customize {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;

      & > *:first-child {
        margin-bottom: 0;
      }
    }

    &__select-item {
      border: none;
    }

    &__select,
    &__color-picker {
      width: calc((100% / 2) - 1%);
    }

    &__select-group {
      width: calc((100% / 2) - 1%);
    }
  }
}

@media only screen and (max-width: $bp-mobile) {
  .project {
    &__customize {
      flex-direction: column;

      & > *:first-child {
        margin-bottom: 2.5rem;
      }
    }

    &__select-group {
      width: 100%;
    }

    &__settings .project__customize {
      margin-bottom: 3.5rem;
    }

    &__select,
    &__color-picker {
      width: 100%;
    }
  }
}

